import Chat from "./Chat";
import ChatInput from "./ChatInput";
import axios from "axios";
import {useEffect, useState} from "react";

const ChatDisplay = ({user, clickedUser}) => {
    const userId = user?.user_id
    const clickedUserId = clickedUser?.user_id
    const [usersMessages, setUsersMessages] = useState(null)
    const [clickedUsersMessages, setClickedUsersMessages] = useState(null)

    const getMessages = async (senderId, recipientId) => {
        try {
            console.log({'userId': userId, 'correspondingUserId': clickedUserId})
            const response = await axios.get('http://localhost:8000/messages', {
                params: {userId: userId, correspondingUserId: clickedUserId}
            })
            setUsersMessages(response.data)

        } catch (e) {
            console.log(e)
        }
    }

    const getClickedMessages = async (senderId, recipientId) => {
        try {
            console.log({'userId': userId, 'correspondingUserId': clickedUserId})
            const response = await axios.get('http://localhost:8000/messages', {
                params: {userId: userId, correspondingUserId: clickedUserId}
            })
            setClickedUsersMessages(response.data)

        } catch (e) {
            console.log(e)
        }
    }

    useEffect(() => {
        getMessages(userId, clickedUserId),
        setClickedUsersMessages(getMessages(clickedUserId, userId))
    }, [usersMessages, clickedUsersMessages])

    const messages = []
    console.log('usersMessages', usersMessages)
    usersMessages?.forEach(message => {
        const formattedMessage = {}
        formattedMessage['name'] = user?.first_name
        formattedMessage['img'] = user?.avatar
        formattedMessage['message'] = message.message_text
        formattedMessage['timestamp'] = message.timestamp
        messages.push(formattedMessage)
    })

    console.log('usersMessages', usersMessages)
    console.log('formattedMessages', messages)

    return (
        <div>
            <Chat/>
            <ChatInput/>
        </div>
    )
}

export default ChatDisplay
