import TinderCard from "react-tinder-card";
import {useEffect, useState} from 'react'
import {useCookies} from 'react-cookie'
import ChatContainer from '../components/ChatContainer'
import axios from "axios";

const Dashboard = () => {
    const [user, setUser] = useState(null)
    const [cookies, setCookie, removeCookie] = useCookies(['user'])
    const [genderedUsers, setGenderedUsers] = useState(null)
    const userId = cookies.UserId
    const getUser = async () => {
        try {
            const response = await axios.get('http://localhost:8000/user', {
                params: {userId}
            })
            if (response.data)
                setUser(response.data)
        } catch (e) {
            console.log(e)
        }
    }

    const getGenderedUsers = async () => {
        //console.log('curUser', user)
        try {
            const response = await axios.get('http://localhost:8000/gendered-users', {
                params: {gender: user?.gender_interest}
            })

            /*if (response.data) {
                Object.values(response.data).map((character) => {
                    console.log(character.first_name)
                })
            }*/
            if (response.data)
                setGenderedUsers(response.data)
        } catch (err) {
            console.log(err)
        }
    }


    useEffect(() => {
        getUser()
        getGenderedUsers()
    }, [user, genderedUsers])

    const [lastDirection, setLastDirection] = useState()

    const swiped = (direction, nameToDelete) => {
        if(direction === 'right')
        setLastDirection(direction)
    }

    const outOfFrame = (name) => {
        console.log(name + ' left the screen!')
    }

    return (
        <>
            {user && <div className="dashboard">
                <ChatContainer user={user}/>
                <div className="swipe-container">
                    <div className="card-container">
                        {
                            genderedUsers &&
                            Object.values(genderedUsers)?.map((genderedUser) =>
                                <TinderCard className='swipe'
                                            key={genderedUser.first_name}
                                            onSwipe={(dir) => swiped(dir, genderedUser.first_name)}
                                            onCardLeftScreen={() => outOfFrame(genderedUser.first_name)}>
                                    <div style={{backgroundImage: 'url(' + genderedUser.avatar + ')'}}
                                         className='card'
                                    >
                                        <h3>{genderedUser.first_name}</h3>
                                    </div>
                                </TinderCard>
                            )
                        }

                        <div className="swipe-info">
                            {lastDirection ? <p>Вы смахнули {lastDirection}</p> : <p/>}

                        </div>
                    </div>
                </div>
            </div>
            }
        </>
    )
}


export default Dashboard
