import TinderCard from "react-tinder-card";
import {useState} from 'react'
import ChatContainer from '../components/ChatContainer'
import axios from "axios";

const Dashboard = () => {
    const [user, setUser] = useState(null)

    const getUser = async () => {
        try{
            const response = await axios.get('http://localhost:8000/user', {
                params: {userId}
            })
            setUser(response.data)
        } catch (e) {
            console.log(e)
        }
    }



    const characters = [
        {
            name: 'Richard Hendricks',
            url: 'https://i.imgur.com/kHKNtki.jpeg'
        },
        {
            name: 'Erlich Bachman',
            url: 'https://i.imgur.com/eCYK8tdb.jpg'
        },
        {
            name: 'Monica Hall',
            url: 'https://i.imgur.com/0gnHZbJb.jpg'
        },
        {
            name: 'Jared Dunn',
            url: 'https://i.imgur.com/tQteAlVb.jpg'
        },
        {
            name: 'Dinesh Chugtai',
            url: 'https://i.imgur.com/FP8psBVb.jpg'
        }
    ]

    const [lastDirection, setLastDirection] = useState()

    const swiped = (direction, nameToDelete) => {
        console.log('removing: ' + nameToDelete)
        setLastDirection(direction)
    }

    const outOfFrame = (name) => {
        console.log(name + ' left the screen!')
    }

    return (
        <div className="dashboard">
            {<ChatContainer/>}
            <div className="swipe-container">
                <div className="card-container">
                    {characters.map((character) =>
                        <TinderCard className='swipe'
                                    key={character.name}
                                    onSwipe={(dir) => swiped(dir, character.name)}
                                    onCardLeftScreen={() => outOfFrame(character.name)}>
                            <div style={{backgroundImage: 'url(' + character.url + ')'}}
                                 className='card'
                            >
                                <h3>{character.name}</h3>
                            </div>
                        </TinderCard>
                    )}

                    <div className="swipe-info">
                        {lastDirection ? <p>Вы смахнули {lastDirection}</p> : <p/>}

                    </div>
                </div>
            </div>
        </div>
    )
}

export default Dashboard
